import React from 'react'
import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider';
import RaisedButton from 'material-ui/RaisedButton';
import TextField from 'material-ui/TextField';
import UserDataService from '../../api/recipes/UserDataService.js';


class RegisterComponent extends React.Component
{
      constructor(props){
        super(props);
        this.state={
          username:'',
          password:'',
          doPasswordsMatch: false,
          showSuccessMessage: false

        }
        //bind methods
        this.handleChange = this.handleChange.bind(this)
        this.registerClicked = this.registerClicked.bind(this)
      }
      //whenever there is a change in a text element an event will handle it
        //event updates the state 
        handleChange(event) {
          this.setState(
              {
                  [event.target.name]
                      :event.target.value
              }
          )
      }


            registerClicked(values){
              let username = values.username;
              let user = {
                id: this.state.id,
                username: values.username,
                password: values.password,


            }
              UserDataService.createUser(username, user)
              .then(() => this.props.history.push(`/login`))
          }



      render() {
        return (
          <div>
            <MuiThemeProvider>
              <div>
              <div>
                  <h1>User Registration</h1>
                   {/* give userfeedback based on credentials */}
                    {/* if true show message */}
                    {this.state.doPasswordsMatch && <div className="alert alert-warning">Passwords do not match</div>}
                    {this.state.showSuccessMessage && <div>Registration Successful</div>}
              </div>
               <TextField
                 hintText="Enter your desired username"
                 floatingLabelText="Username"
                 value={this.state.username} onChange={this.handleChange}
                 />
               <br/>
               <TextField
                 type = "password"
                 hintText="Enter your desired password"
                 floatingLabelText="Password"
                 value={this.state.password} onChange={this.handleChange}
                 />
               <br/>
               {/* <TextField
                 type = "password"
                 hintText="Retype your password"
                 floatingLabelText="Confirm Password"
                 onChange = {(event,newValue) => this.setState({password2:newValue})}
                 />
               <br/> */}
               <RaisedButton label="Submit" primary={true} style={style} onClick={this.registerClicked}/>
              </div>
             </MuiThemeProvider>
          </div>
        );
      }
    }
    const style = {
      margin: 15,
    };

export default RegisterComponent;
