import React, {Component} from 'react'

class ToDoApp extends Component {
    render() {
        return (
            <div className="ToDoApp">
                <LoginComponent/>
            </div>
        )
    }
}





class LoginComponent extends Component {
    constructor(props) {
        super(props)
        // added state to component
        this.state = {
            username: 'username',
            password: '',
            hasLoginFailed: false,
            showSuccessMessage: false

        }
        //bind methods
        this.handleChange = this.handleChange.bind(this)
        this.loginClicked = this.loginClicked.bind(this)


        }
        //whenever there is a change in a text element an event will handle it
        //event updates the state 
        handleChange(event) {
            this.setState(
                {
                    [event.target.name]
                        :event.target.value
                }
            )
        }

        loginClicked(){
            //username: user, password: user
            //hardcoded for now change later
            if(this.state.username=== 'user' && this.state.password=== 'user'){
                // console.log('success!')
                this.setState({showSuccessMessage:true})
                this.setState({hasLoginFailed:false})
            } else {
                // console.log('failed')
                this.setState({showSuccessMessage:false})
                this.setState({hasLoginFailed:true})
            }
            // console.log(this.state)
        }

    render() {
        return (
            <div>
                {/* hardcoded messages to give userfeedback based on credentials */}
                {this.state.hasLoginFailed && <div>Invalid Credentials</div>}
                {this.state.showSuccessMessage && <div>Login Successful</div>}

                {/* //value of buttons are tied back to the value of this state */}
                Username: <input type="text" name="username" value={this.state.username} onChange={this.handleChange}/>

                Password: <input type="password" name="password" value={this.state.password} onChange={this.handleChange}/>
                <button onClick={this.loginClicked}>Login</button>
            </div>
        )
    }


}

function ShowInvalidCredentials(props){
        if(props.hasLoginFailed) {
            return <div>Invalid Credentials</div>
        }
        return null
}

function ShowLoginSuccessMessage(props){
    if(props.showSuccessMessage){
        return <div>Login Successful</div>
    }
    return null
}

export default ToDoApp
