import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import logo from "./logo2.png"



class ContactComponent extends Component {

    constructor(props) {
        super(props)
        this.handleSuccessfulResponse = this.handleSuccessfulResponse.bind(this)
        this.handleError = this.handleError.bind(this)
        this.state = {
            welcomeMessage: '',
            errorMessage: ''

        }
    }
    render() {
        return (
            <>
                <div>

                </div>
                <br></br>
                <img src={logo} width="200" alt="github.com/klayne1129" />
                <br></br>

                <div className="container">
                    <h2>You can contact me here:</h2>
                    <h3>You can find me on:</h3>
                    <div id="list">
                        <ul>
                            <li><a href="https://www.teacherspayteachers.com/Store/Speech-Keys">TeachersPayTeachers</a></li>
                        </ul>
                    </div>
                    <p>Or, you can <a href="mailto:klayne1129@gmail.com">send me an email</a>.</p>
                </div>

                {/* <div className="container">
                            Click here for a customized welcome message. 
                            <button onClick={this.retrieveWelcomeMessage}
                                 className="btn btn-success">Welcome Message</button>
                        </div> */}
                <div className="container">
                    {this.state.welcomeMessage}
                </div>
                {/* errors */}
                {/* <div className="container">
                            {this.state.errorMessage}
                        </div> */}

            </>

        )
    }

    // call the promise method. Once called THEN define what should be done with a
    // successful response
    // retrieveWelcomeMessage() {
    //     // HelloWorldService.executeHelloWorldService()
    //     // .then( response => this.handleSuccessfulResponse(response) )

    //     // HelloWorldService.executeHelloWorldBeanService()
    //     // .then( response => this.handleSuccessfulResponse(response) )

    //     HelloWorldService.executeHelloWorldPathVariableService(this.props.match.params.name)
    //     .then( response => this.handleSuccessfulResponse(response) )
    //     .catch( error => this.handleError(error))
    // }

    handleSuccessfulResponse(response) {
        this.setState({ welcomeMessage: response.data.message })
    }




    handleError(error) {
        console.log(error.response)
        let errorMessage = '';
        if (errorMessage) {
            errorMessage += error.message
        }

        if (error.response && error.response.data) {
            errorMessage += error.response.data.message
        }
        this.setState({ errorMessage: errorMessage })
    }

}


export default ContactComponent
