import React, {Component} from 'react'
import AuthenticationService from './AuthenticationService.js'
import RecipeDataService from '../../api/recipes/RecipeDataService.js'


class ViewRecipeComponent extends Component{
    constructor(props){
        super(props)
        this.state = {
            id: this.props.match.params.id,
            title: '',
            directions: '',
            ingredients: '',
            notes: '',
            mealType: ''
        }
        this.refreshRecipe = this.refreshRecipe.bind(this);
    }
    componentDidMount() {

        console.log("component did Mount")
        this.refreshRecipe();
    }

    refreshRecipe(){

        if (this.state.id === -1) {
            return
        }

        let username = AuthenticationService.getLoggedInUser()

        RecipeDataService.retrieveRecipe(username, this.state.id)
            .then(response => this.setState({
                title: response.data.title,
                directions: response.data.directions,
                ingredients: response.data.ingredients,
                notes: response.data.notes,
                mealType: response.data.mealType
            }))
    }


    render() {
        return  (   

<div className="container">
                            <table className="table">
                                <thead>
                                    <tr>
                                        <th>Title</th>
                                        <th>Directions</th>
                                        <th>Ingredients</th>
                                        <th>Notes</th>
                                        <th>Meal Type</th>
                                    </tr>
                                </thead>
                                <tbody>
                                            <tr>
                                                <td><h2 className='display-4'>{this.state.title}</h2></td>

                                                <td><p className='lead'><ul>{this.state.directions}</ul></p></td>

                                                <td><p className='lead'><ul>{this.state.ingredients}</ul></p></td>

                                                <td><p className='lead'><ul>{this.state.notes}</ul></p></td>

                                                <td><p className='lead'><ul>{this.state.mealType}</ul></p></td>
                                            </tr>


                                </tbody>
                            </table>
                            <div className="row">
                            <button className="btn btn-success" value="Print" onClick={() => this.printRecipeClicked()}>Print</button>
                            </div>
                        </div>
        )
    }


    }






