import React, {Component} from 'react'
import AuthenticationService from './AuthenticationService.js'
import RecipeDataService from '../../api/recipes/RecipeDataService.js'


class ViewRecipeComponent extends Component{
    constructor(props){
        super(props)
        this.state = {
            id: this.props.match.params.id,
            title: '',
            directions: '',
            ingredients: '',
            notes: '',
            mealType: ''
        }
        this.refreshRecipe = this.refreshRecipe.bind(this);
    }
    componentDidMount() {

        console.log("component did Mount")
        this.refreshRecipe();
    }

    refreshRecipe(){

        if (this.state.id === -1) {
            return
        }

        let username = AuthenticationService.getLoggedInUser()

        RecipeDataService.retrieveRecipe(username, this.state.id)
            .then(response => this.setState({
                title: response.data.title,
                directions: response.data.directions,
                ingredients: response.data.ingredients,
                notes: response.data.notes,
                mealType: response.data.mealType
            }))
    }


    render() {
        return  (   
            <div className='container d-flex' style={{
                marginTop: 65,
            }}
             role='main'>
                <div className='row mt-4'>
                    <div className='col'>
                        <h1 className='display-4'>{this.state.title}  </h1>


                    </div>
                    <div className='col' style={{paddingTop: 60}}>
                        <h4 className='display-4'>Ingredients:</h4>
                        <p className='lead'><ul>{this.state.ingredients}</ul></p>

                        <h4 className='display-4'>Steps:</h4>
                        <p className='lead'><ul>{this.state.directions}</ul></p>

                        <h4 className='display-4'>Notes:</h4>
                        <p className='lead'><ul>{this.state.notes}</ul></p>

                        <h4 className='display-4'>Meal Type:</h4>
                        <p className='lead'><ul>{this.state.mealType}</ul></p>


                    </div>

            </div>





            </div>
                )
    }
}


export default ViewRecipeComponent
